function Offer() {
  let [showX, setShowX] = useState(false);

  function handleClick() {
    setShowX(!showX);
    return "ShowX is: " + !showX;
  }

  return (
    <div>
      <h1>HOOK TEST</h1>
      <h2>{showX}</h2>
      <button onClick={handleClick}>ShowX</button>
    </div>
  );
}

export default Offer;
